#include <stddef.h> // size_t
#include <string.h> // strlen //	unused
#include <unistd.h> // write
//#include <stdio.h> // putc, putchar, puts, printf //	unused //	debug

#undef strlen
#undef putn
#undef uputn
#undef ullputn

size_t strlen(const char* s)
{
	const char* p = s;

	while ( *++p );
	return ( p - s );
} // KVPB's STRLEN

int putn( int n, char* b)
{
	int i = 0;
	int l = strlen( b );

	if (  n  <  0 )
	{
		n = -( n );
		i += write(1, "-", 1);
	}
	if (  n  <  l )
		i += write(1, &b[n], 1);
	else
	{
		i += putn( n / l, b );
		i += putn( n % l, b );
	}
	return i;
} // KVPB's PUTN
