int uputn( unsigned int n, char* b)
{
	int i = 0;
	unsigned int l = strlen( b );

	if (  n  <  l )
		i += write(1, &b[n], 1);
	else
	{
		i += uputn( n / l, b );
		i += uputn( n % l, b );
	}
	return i;
} // KVPB's UPUTN

int ullputn( unsigned long long n, char* b)
{
	int i = 0;
	unsigned long long l = strlen( b );

	if (  n  <  l )
		i += write(1, &b[n], 1);
	else
	{
		i += ullputn( n / l, b );
		i += ullputn( n % l, b );
	}
	return i;
} // KVPB's ULLPUTN

